func TestDayThree2021PartOneExample(t *testing.T) {

	input := []string{"vJrwpWtwJgWrhcsFMMfFFhFp", "jqHRNqRjqzjGDLGLrsFMfFZSrLrFZsSL", "PmmdzqPrVvPwwTWBwg", "wMqvLMZHhHMvwLHjbvcjnnSBnvTQFn", "ttgJtRGJQctTZtZT", "CrZsJsPPZsGzwwsLwLmpwMDw"}

	actual := rePrioPacking(input)
	expected := 157

	assert.Equal(t, expected, actual)
}

func rePrioPacking(packings []string) int {
	totPrio := 0
	for i, pack := range packings {
		fmt.Printf("Pack #%d: %s \n", i, pack)
		runes := []rune(pack)
		compartmentA := runes[:len(runes)/2]
		compartmentB := runes[len(runes)/2:]
		var dupe rune
		for _, c := range compartmentA {
			if strings.Contains(string(compartmentB), string(c)) {
				dupe = c
				break
			}
		}
		var prio int
		if dupe >= 97 {
			prio = int(dupe) - 96
		} else {
			prio = int(dupe) - 38
		}
		fmt.Printf("compartmentA: %s, compartmentB: %s => dupe: %s (%d) \n", string(compartmentA), string(compartmentB), string(dupe), prio)
		totPrio += prio
	}
	return totPrio
}

