func findGroupBadge(packings []string) int {
	totPrio := 0
	for i := 0; i < len(packings); i = i + 3 {
		var dupe rune
		for _, c := range packings[i] {
			if strings.Contains(packings[i+1], string(c)) && strings.Contains(packings[i+2], string(c)) {
				dupe = c
				break
			}
		}
		var prio int
		if dupe >= 97 {
			prio = int(dupe) - 96
		} else {
			prio = int(dupe) - 38
		}
		fmt.Printf("A: %s, B: %s, C: %s => dupe: %s (%d) \n", packings[i], packings[i+1], packings[i+2], string(dupe), prio)
		totPrio += prio
	}
	return totPrio
}

